import "./Header.css";
import React from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import { Link } from "react-scroll";
import logo from "../../images/FGlogo.png";
import Image from "react-bootstrap/Image";


const [red, green, blue] = [69, 111, 225]
const header = document.querySelector('.header')

window.addEventListener('scroll', () => {
  const y = 1 + (window.scrollY || window.pageYOffset) / 150
  const [r, g, b] = [red/y, green/y, blue/y].map(Math.round)
  header.style.backgroundColor = `rgb(${r}, ${g}, ${b})`
})

const Header = ({}) => {
  return (
    <Navbar className="header" bg="transparent" fixed="top" expand="lg">
      <Navbar.Brand href="/">
        {" "}
        <Image src={logo} className="logo" roundedCircle alt="FG" />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="nav-items ml-auto">
          <a className="nav-item" href="/projects">
            Projects
          </a>
          <Link
            className="nav-item"
            activeClass="active"
            to="education"
            spy={true}
            smooth={true}
            offset={-20}
            duration={500}
          >
            Education
          </Link>
          <Link
            className="nav-item"
            activeClass="active"
            to="experience"
            spy={true}
            smooth={true}
            offset={-20}
            duration={500}
          >
            Experience
          </Link>
          <Link
            className="nav-item"
            activeClass="active"
            to="skills"
            spy={true}
            smooth={true}
            offset={-20}
            duration={500}
          >
            Skills
          </Link>
          <Link
            className="nav-item"
            activeClass="active"
            to="achievements"
            spy={true}
            smooth={true}
            offset={-20}
            duration={500}
          >
            Achievements
          </Link>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
};

export default Header;
