import "./Header.css";
import React from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import { Link } from "react-scroll";
import logo from "../../images/FGlogo.png";
import Image from "react-bootstrap/Image";

class Header extends React.Component {
  constructor(props) {
    super(props);

    this.listener = null;
    this.state = {
      status: "top",
      prevScrollpos: window.pageYOffset,
      visible: true,
    };
  }

  componentDidMount() {
    this.listener = document.addEventListener("scroll", (e) => {
      var scrolled = document.scrollingElement.scrollTop;
      if (scrolled >= 760) {
        if (this.state.status !== "scrolled") {
          this.setState({ status: "scrolled" });
        }
      } else {
        if (this.state.status !== "top") {
          this.setState({ status: "top" });
        }
      }
    });
  }

  componentDidUpdate() {
    document.removeEventListener("scroll", this.listener);
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
  }

  handleScroll = () => {
    const { prevScrollpos } = this.state;

    const currentScrollPos = window.pageYOffset;
    const visible = prevScrollpos > currentScrollPos;

    this.setState({
      prevScrollpos: currentScrollPos,
      visible
    });
  };
  render() {
    return (
      <Navbar
        style={{
          backgroundColor:
            this.state.status === "top" ? "transparent" : "black",
          position:
            this.state.visible === "true" && this.state.status === "top"
              ? "static"
              : "fixed",
          top: this.state.visible === "true" ? "0" : "-100px",
        }}
        className="header"
        fixed="top"
        expand="lg"
      >
        <Navbar.Brand href="/">
          {" "}
          <Image src={logo} className="logo" roundedCircle alt="FG" />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="nav-items ml-auto">
            <a className="nav-item" href="/projects">
              Projects
            </a>
            <Link
              className="nav-item"
              activeClass="active"
              to="education"
              spy={true}
              smooth={true}
              offset={-20}
              duration={500}
            >
              Education
            </Link>
            <Link
              className="nav-item"
              activeClass="active"
              to="experience"
              spy={true}
              smooth={true}
              offset={-20}
              duration={500}
            >
              Experience
            </Link>
            <Link
              className="nav-item"
              activeClass="active"
              to="skills"
              spy={true}
              smooth={true}
              offset={-20}
              duration={500}
            >
              Skills
            </Link>
            <Link
              className="nav-item"
              activeClass="active"
              to="achievements"
              spy={true}
              smooth={true}
              offset={-20}
              duration={500}
            >
              Achievements
            </Link>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    );
  }
}

export default Header;
